Much faster version of previous code

This code produces the same output as the previous code but is much faster.

%let pgm=utl_optlen;

/* set the length of all char variables in a sas dataset to longest observed value */

%macro utl_optlen(dsin,globvar=_optlength_)
        / des="Create a length statement to optimise a datasets attributes";

    %global &globvar;
    %let &globvar=;

    * get the number of numeric and number of character variables for array statement;
    data _null_;
       retain dsin "&dsin";
       length libref $8 dsn $32;
       set sashelp.vtable;
       if index(dsin,'.') = 0 then do; libref='WORK';                   dsn=upcase(dsin);            end;
       else do;                        libref=upcase(scan(dsin,1,'.')); dsn=upcase(scan(dsin,2,'.'));end;
       if       libname=libref
           and  memname=dsn
       ;
       call symputx('num',put(num_numeric,  8.));
       call symputx('chr',put(num_character,8.));
    run;

    %put &=num;
    %put &=chr;

    data _null_;

       set &dsin end=dne;

       /*
        A little flaky with all the macro if statements but
        you have to be careful not to pick up variables
        that do not exist in the source dataset but are part og this code like variable I
       */

       %if &num ne 0 %then %do;

         array num[&num]    _numeric_  ;
         array lennum[&num] _temporary_;

       %end;

       %if &chr ne 0 %then %do;

         array chr[&chr] _character_;
         array lenchr[&chr] _temporary_;

         do i=1 to dim(chr);
            if lengthn(chr[i]) > lenchr[i] then lenchr[i]=lengthn(chr[i]);
         end;

       %end;


       * do not want this to be in the char array;
       * a length statement will include it;

       length macvar $32000;

       %if &num ne 0 %then %do;

         do i=1 to dim(num);

            if num[i] ne trunc( num[i], 7 ) then len = 8 ; else
            if num[i] ne trunc( num[i], 6 ) then len = 7 ; else
            if num[i] ne trunc( num[i], 5 ) then len = 6 ; else
            if num[i] ne trunc( num[i], 4 ) then len = 5 ; else
            if num[i] ne trunc( num[i], 3 ) then len = 4 ; else len=3;
            if len > lennum[i] then lennum[i]=len;
          end;

       %end;

       if dne then do;
         %if &chr ne 0 %then %do;
          do i=1 to dim(chr);
            macvar=catx(' ',macvar,vname(chr[i]),cats('$',put(lenchr[i],6.)));
          end;
         %end;

         %if &num ne 0 %then %do;
          do i=1 to dim(num); * do not want I variable;
            macvar=catx(' ',macvar,vname(num[i]),put(lennum[i],6.));
          end;
         %end;

          call symputx("&globvar",macvar);
       end;

      run;

      %put &&&globvar;

%mend utl_optlen;

