Weak reversible encryption of integers

see utl_encrypt.sas in pinned files for code

ptions cmplib = (work.functions /* add other libs here */);

* ENCRYPT A LARGE INTEGER - SHOULD HANDLE 1->107,199,254,740,992;
proc fcmp outlib=work.functions.hashssn;
function encrypt(ssn);
    key=8192;
    ssn_remainder = mod(ssn, key);
    ssn_int       = round((ssn - ssn_remainder)/key,1);
    * 8192  ssn_remainder = 1 and ssn_int = 1 ie 1*key + 1 = original value;
    ssn_encrypt   = ssn_int*100000 + ssn_remainder;
  return(ssn_encrypt);
endsub;
run;quit;

proc fcmp outlib=work.functions.hashssn;
function decrypt(ssn);
    key=8192;
    ssn_decrypt   = round(ssn/100000,1)*key + mod(ssn,10000);
  return(ssn_decrypt);
endsub;
;run;quit;


