%macro top_100getptb(firstobs=,obs=);
  %put &=syshostname &=sysncpu;
  * try to avoid the slower unix boxes
    especially SYSHOSTNAME=xxxxxxxx SYSNCPU=xx;
  libname top "&_r/top";
  data xxxxxxxxxxxxxxxxxxx&firstobs;;
     set xxxxxxxxxxxxxxxxxxxxxxxxxxxxxx(
          firstobs=&firstobs
          obs=&obs
          keep=
           xxx
           state_county_fips_prov
           xxxxxxxxxxx
           hcpcs_cd
           xxxxxx
           srvc_month
           clm_id
           xxxxxxxx
           xxxxxx
