%macro utl_sendadr(var=avgwgt)
     /des="create new variable avgwgt and its addree to send to dosubl";

   * find the address of &var and load into a macro variable;
   * __chradr is a global macro variable;

   retain avgwgt . ;
   length __chr $40;

   __getadr=addrlong(&var);
   __chr=put(__getadr,$hex40.);
   call symputx("__chradr",__chr,'G');
   drop __getadr __chr;

%mend utl_sendadr;

%macro utl_retadr(var=avgkilo)
       / des="Load the address sent from sendadr with the average class weight";

         * poke the average into the address;

         length __extadr $20;
         __res="&__CHRADR";
         do __i=1 to 39 by 2;
           __chr1=byte(input(substr(__res,__i,2),hex2.));
           __extadr=cats(__extadr,__chr1);
         end;
         __ret=strip(put (&var, rb8.));
         call pokelong ( __ret, __extadr, 8);
         drop __extadr __res __chr1 __i __ret;

%mend utl_retadr;

