%macro oto_caliml(
    /* this type of quating allows for embeded macro variables in RPG */
    /* always remember R is case sensitive */
    /* you may want to use a space before the sas dataset */
    rpg=
     %unquote(%str(%'
        carsum.saso01<-sapply(cars.sasi01, mean, na.rm=TRUE);
        z.o1 <- aov( &dep ~ SEX + HEIGHT,data=class.sasi02);
        RESID<-z.o1$residuals;
        FIT<-z.o1$fitted.values;
        residual.saso02<-cbind(class.sasi01,RESID,FIT);
        cof<-z.o1$coefficients;
        coef.saso03=data.frame(names(cof),cof);
    %'));
  )/des="Interface with IML to run R - currently allows up to 5 sas input datasets and 5 output datasets";

  /*
    gotchas

    input  and output sas datasets have be in the work directory and must have a sufix of either
    saso0X for output and sasi0x fro input

    do not use these strings in your R code except for identifying input and output SAS data sets

    .sasi01','.sasi02','.sasi03','.sasi04','.sasi05
    .saso01','.saso02','.saso03','.saso04','.saso05

  * testcase;
  %let
    rpg=
     %unquote(%str(%'
        carsum.saso01<-sapply(cars.sasi01, mean, na.rm=TRUE);
        z.o1 <- aov( &dep ~ SEX + HEIGHT,data=class.sasi02);
        RESID<-z.o1$residuals;
        FIT<-z.o1$fitted.values;
        residual.saso02<-cbind(class.sasi01,RESID,FIT);
        cof<-z.o1$coefficients;
        coef.saso03=data.frame(names(cof),cof);
    %'));

  */

  filename tmp temp ;

  DATA _null_;
    retain numinp numout 0;
    length rpg $32750 fininp finout $10000 inp out $200;
    file tmp;
    i=1;
    rpg=&rpg;

    * create @ delimited list of input datasets - note for rpg thua is cars@class;

    do txt='.sasi01','.sasi02','.sasi03','.sasi04','.sasi05';
       pos=index(rpg,txt);
       if pos>0 then do;
          nam=scan(substr(rpg,1,pos),-1,'. =-()[]{}<>');
          inp=catx('@',inp,nam);
          numinp=numinp+1;
       end;
       else leave;
    end;
    putlog inp=;

    /* inp=cars@class */

    do txt='.saso01','.saso02','.saso03','.saso04','.saso05';
       pos=index(rpg,txt);
       if pos>0 then do;
          nam=scan(substr(rpg,1,pos),-1,'. =-()[]{}<>');
          out=catx('@',out,nam);
          numout=numout+1;
       end;
       else leave;
    end;
    putlog out=;

    /* OUT=carsum@residual@coef */

    do i=1 to numinp;
      fininp=cats(fininp,"call exportdatasettor('",strip(scan(inp,i,'@')),"','",strip(scan(inp,i,'@')),"');");
    end;

    putlog fininp=;

    /* FININP=call exportdatasettor('cars','cars');call exportdatasettor('class','class'); */

    do i=1 to numout;
      finout=cats(finout,"call importdatasetfromr('",strip(scan(out,i,'@')),"','",strip(scan(out,i,'@')),"');");
    end;

    putlog finout=;

    /*
    FINOUT=call importdatasetfromr('carsum','carsum');
           call importdatasetfromr('residual','residual');
           call importdatasetfromr('coef','coef');
    */

    * remove .sasi0x and .saso0x;
    rpg=prxchange('s/\.sas[i|o]0\d{1}//',99,rpg);

    putlog rpg=;
      /*
        RPG=carsum<-sapply(cars, mean, na.rm=TRUE);
        z.o1 <- aov( WEIGHT ~ SEX + HEIGHT,data=class);
        RESID<-z.o1$residuals;
        FIT<-z.o1$fitted.values;
        residual<-cbind(class,RESID,FIT);
        cof<-z.o1$coefficients;
        coef=data.frame(names(cof),cof);
      */

    * put it all together;

    put "proc iml;" / fininp  /  "submit / R;";
    i=1;
    do  until (scan(rpg,i,';')='');
      lyn=scan(rpg,i,';')!!';';
      put lyn;
      i=i+1;
    end;
    put 'endsubmit;'  /  finout  /  ';quit;';

    /*
    proc iml;
    call exportdatasettor('cars','cars');
    call exportdatasettor('class','class');
    submit / R;
    carsum<-sapply(cars, mean, na.rm=TRUE);
    z.o1 <- aov( WEIGHT ~ SEX + HEIGHT,data=class);
    RESID<-z.o1$residuals;
    FIT<-z.o1$fitted.values;
    residual<-cbind(class,RESID,FIT);
    cof<-z.o1$coefficients;
    coef=data.frame(names(cof),cof);
    endsubmit;
    call importdatasetfromr('carsum','carsum');
    call importdatasetfromr('residual','residual');
    call importdatasetfromr('coef','coef');
    run;
    quit;
    */

