/* create some data */
data stucor;
  retain student 1 one 1;
  keep student grade student;
  do grade1='A','B','C','D';
     do seq=1 to 100;
       cnt=0;
       do grade2='A','B','C','D';
       if grade2='A' then student=student+1;
        cnt=cnt+1;
        tst=uniform(5733);
        if tst < .80 then do;
             if uniform(5732)<.5 then grade=grade1;
             else grade=grade2;
             if mod(cnt-1,4)=1 then do; course='G'; course_des='Algebra'; end;
             if mod(cnt-1,4)=2 then do; course='H'; course_des='History'; end;
             if mod(cnt-1,4)=3 then do; course='S'; course_des='Science'; end;
             if mod(cnt-1,4)=0 then do; course='P'; course_des='Polysci'; end;
             grade_course=cats(grade,course);
             keep student course course_des  grade grade_course one;
             output;
         end;
      end;
  end;
 end;
