%utl_submit_r(%nrbquote(
library (sas7bdat);
library(stringr);
/* convert sas dataset to R dataframe */
grades<-read.sas7bdat("c:\\temp\\duplets.sas7bdat");
/* create the vector "A" "B" "C" "D" */
/* these are the names of the SAS columns */
abcd<-names(grades);
/* form all combinations of the for letters A,B / A,C / A,D / B,C / B,D / C, D */
allcmb<-expand.grid(rep(list(abcd), 2));
/* paste A with B to get AB and .. C with D to get CD */
grade <- as.data.frame(do.call("paste", c(grades[abcd], sep='')));
names(grade)<-"abcd";
/* place holder for final crosstab counts */
ans<-as.data.frame(t(rep(NA,nrow(allcmb))));
names(ans)<-"crosstab";
/* loop through all the combinations checking all students at once */
for ( r in 1:nrow(allcmb) ) {
   ans[r]<-sum(is.na(str_match(grade$abcd,as.character(allcmb[r,1])))==FALSE &
           is.na(str_match(grade$abcd,as.character(allcmb[r,2])))==FALSE )
    };
/* need to have meaningfull names for final crosstab */
names(ans)<-paste(allcmb[,1],allcmb[,2],sep='');
ans;
));

