* US map with poverty rate;
* http://colorbrewer2.org/  greate place for color schemes;
* requires SAS maps datasets maps.uscenter and maps.us;
%macro utl_100usmap(
          var=poverty_rate
         ,inp=poverty2010
         ,tyt=2010 US Poverty Rate
         );

    ods exclude all;

    * label states. Note use of ocean for small notheast states;
    * this is the hard part;
    * you can make utl_100ctr a perm dataset and remove this datastep;
    * labeling is always the hard part;

    data utl_100ctr;

      set maps.uscenter;

       st=fipstate(state);

       length function $ 8 style color cbox cborder $ 12 position $ 1 text $ 100;
       retain flag 0 function 'label' xsys '2' ysys '2' hsys '3' when 'a' position '5';

     function='label'; text=trim(left(st)); size=2; position='5';
     style='"arial/bold"';

     * if we need label to extend over the ocean;
     if ocean='Y' then
        do;
         function='label';
         style='"arial/bold"';
         color='black';
         cborder='gray99';
         position='6';
         cbox='GRAYFF';
         text=st;
         output;
         function='move';
         flag=1;
        end;
     else if flag=1 then
        do;
           function='draw'; size=.05;
           flag=0;
        end;
     output;
    run;

    * join labeled states with input data;
    proc sql;
      create
         table utl_100cmb as
      select
         l.*
        ,r.*
      from
         utl_100ctr as l left join &inp as r
      on
         l.text = r.state
    ;quit;

    pattern1 value=solid color='CXF0F9E8'  ;
    pattern2 value=solid color='CXBAE4BC'  ;
    pattern3 value=solid color='CX7BCCC4'  ;
    pattern4 value=solid color='CX43A2CA'  ;
    pattern5 value=solid color='CX0868AC'  ;

    ods select all;
    Legend1 label=(height=3 position=top justify=left "&tyt");
    proc gmap map=maps.us  data=utl_100cmb ;
    title h=3 "&tyt";
    label &var=" ";
    format &var comma15.;
         id state;
         choro &var / levels=5 legend=legend1 annotate=utl_100ctr legend=legend1
             cempty=white cdefault=white coutline=black;
    run;
    quit;

%mend utl_100usmap;

