SAS Innovation: Placing counts in proc report column headers 

Suppose you want to produce the following
 report with the counts of males and females
 in the header.

  For a cleaner copy of theis message see
  https://www.yammer.com/hhs.gov/uploaded_files/34524952

  utl_counts_in_header


   /*******************************************\
   *                                          *
   *               Female          Male       *
   *    GOV        N = 21        N = 29       *
   *                                          *
   *    CIA             5             9       *
   *    FBI             6            10       *
   *    IRS            10            10       *
   *                                          *
   \******************************************/


   * create sample data;
   Data  SexGov;
   Do Sex='Female','Male';
     Do Gov='FBI','CIA','IRS';
       Do Ids=10 to 25;
           Serial=Int(1E6*Uniform(5739));
           Pay=Int(50000*Uniform(57343));
           If Uniform(5643) < .5 Then Output;
         End;
       End;
     End;
   Run;

   /*---------------------------------------------*\
   | Output                                        |
   |   Sex      Gov    Ids       Pay               |
   |  Female    FBI     12    22831.13             |
   |  Female    FBI     14    16942.24             |
   |  Female    FBI     15     9239.63             |
   |  Female    FBI     20    27425.14             |
   |  Female    FBI     21     8424.24             |
   |  Female    FBI     22     9305.96             |
   |  Female    FBI     23    25826.22             |
   \*---------------------------------------------*/

   * create macro variable Male with  "Male # N=29";
   proc sql;
      select resolve(catx(' ','%let',sex,'=%str(',sex,'# N =',put(count(*),2. -l),');'))
      from sexgov group by sex;quit;

   %put &=female;
   %put &=male;

   /*
   FEMALE= Female # N = 21
   MALE  = Male # N = 29
   */

   /* Put counts in Rectangular array */
   Ods Exclude All;
   Ods Output Observed=GovSexTbl(Rename=Label=Gov
       drop=sum where=(gov ne 'Sum'));
   Proc Corresp Data=SexGov Observed dim=1;
      Table Gov, Sex;
   Run;
   Ods Select All;

   * print report note the macro variables;
   proc report data=GovSexTbl nowd missing split='#' headskip;
     cols Gov Female Male;
     define Female /display "&female" width=12;
     define Male   /display "&male" width=12;
   run;quit;

