   Do Sex='F','M';
     Do Gov='FBI','CIA','IRS';
       Do Ids=10 to 25;
           Serial=Int(1E6*Uniform(5739));
           Pay=Int(50000*Uniform(57343));
           If Uniform(5643) < .5 Then Output;
         End;
       End;
     End;
   Run;

   /*
   Up to 40 obs WORK.SEXGOV total obs=50

   Obs    GOV    SEX    IDS    SERIAL     PAY

     1    FBI     F      13     25439     3463
     2    FBI     F      14    629949    28614
     3    FBI     F      17    851828    27102
     4    FBI     F      20     66992    19882
     5    FBI     F      23    659046    34665
     6    FBI     F      25    248322    17126
     7    CIA     F      13    102059     4111
     8    CIA     F      14    951256    28675
     9    CIA     F      18    440843    34973
    */

   proc format;
   value $gender(notsorted multilabel)
    'M'='Male'
    'F'='Female'
    'U'='Unknown'
    'U','M','F'='AllGender';
   run;

   proc summary data=SexGov nway completetypes;
     class gov;
     class  sex / order=data mlf preloadfmt;
     format sex $gender.;
     output out=BigCnt / levels;
   run;

