/* Running C source code and Java from SAS

In C run

  1. length of a SAS string
  2. first char
  3. last char
  4. ending null char

In Java concatenate two strings (proc Proto)

Using Groovy to get the number of pts for an
arbitary proportional font for a SAS string

*/

***********************
***********************
PROTO
***********************
***********************;

/* C functions*/
proc proto package=sasuser.funcs.sfuncs;

    /* A string length function */
    int cslen(const char *s);
    externc cslen;
    int cslen(const char *s)
    {
        int i=0;
        while (s[i++]!=0){}
        return i-1;
    }
    externcend;

    /* A char function */
    int cschar(const char *s,const int pos);
    externc cschar;
    int cschar(const char *s,const int pos)
    {
        return s[pos];
    }
    externcend;
run;

option cmplib=sasuser.funcs;
/* SAS wrappers */
proc fcmp outlib=sasuser.funcs.sfuncs;

    function slen(s $);
        /* call C lenth function - length of string*/
        val=cslen(trim(s));
        return(val);
    endsub;

    function schar(s $,pos);
        /* call C schar function - specific byte*/
        val=cschar(s,pos);
        return(val);
    endsub;
quit;

/* Tests */
data _null_;
    length str $9.;
    str="foobarlen";
    len=slen(str);
    firstchar=schar(str,0);
    lastchar=schar(str,8);
    shouldbenull=schar(str,9);
    put _all_;
run;

***********************
***********************
CONCATENATE SAS STRINGS
***********************
***********************;

data _null_;
   length s_out $200;
   first='ABCDE';
   second='FGHIJ';
   declare JavaObj j1 ('java/lang/String',first);
   declare JavaObj j2 ('java/lang/String',second);
   j1.callStringMethod ('concat', j2, s_out);
   put s_out=;
   j1.delete(); j2.delete();
run;

