   %put %sysfunc(ifc(%sysevalf(%superq(outdsn )=,boolean),**** Please Provide an output dataset    ****,));

    %let res= %eval
    (
        %sysfunc(ifc(%sysevalf(%superq(sysbin )=,boolean),1,0))
      + %sysfunc(ifc(%sysevalf(%superq(outdsn )=,boolean),1,0))
    );

